mapper.select:
select id, user_name userName, user_password userPassword, user_email userEmail, user_info userInfo, head_img headImg, create_time createTime from sys_user
and user_name like concat('%', #{userName}, '%')
and user_email = #{userEmail}

select id, user_name userName, user_password userPassword, user_email userEmail, user_info userInfo, head_img headImg, create_time createTime from sys_user where 1 = 1
and id = #{id}
and user_name = #{userName}
limit 0

{call select_user_by_id(
	#{id, mode=IN}, 
	#{userName, mode=OUT, jdbcType=VARCHAR},
	#{userPassword, mode=OUT, jdbcType=VARCHAR},
	#{userEmail, mode=OUT, jdbcType=VARCHAR},
	#{userInfo, mode=OUT, jdbcType=VARCHAR},
	#{headImg, mode=OUT, jdbcType=BLOB, javaType=_byte[]},
	#{createTime, mode=OUT, jdbcType=TIMESTAMP}
)}

{call select_user_page(
	#{userName, mode=IN},
	#{offset, mode=IN},
	#{limit, mode=IN},
	#{total, mode=OUT, jdbcType=BIGINT}
)}

mapper.insert:
{call insert_user_and_roles(
	#{user.id, mode=OUT, jdbcType=BIGINT}, 
	#{user.userName, mode=IN},
	#{user.userPassword, mode=IN},
	#{user.userEmail, mode=IN},
	#{user.userInfo, mode=IN},
	#{user.headImg, mode=IN, jdbcType=BLOB},
	#{user.createTime, mode=OUT, jdbcType=TIMESTAMP},
	#{roleIds, mode=IN}
)}
